function openPanel(e){closeAllPanels();let l=document.getElementById(e+"-panel"),s=document.getElementById("hero");l&&(l.classList.add("active"),s.classList.add("dimmed"),document.body.classList.add("menu-open"))}function closeAllPanels(){let e=document.querySelectorAll(".panel"),l=document.getElementById("hero");e.forEach(e=>e.classList.remove("active")),l.classList.remove("dimmed"),document.body.classList.remove("menu-open")}document.addEventListener("keydown",function(e){"Escape"===e.key&&closeAllPanels()});